import React, { useEffect, useState } from "react";
import classes from "./ResultPage.module.css";
import axios from "axios";
import Confetti from "react-confetti";

function ResultPage(props) {
  const answerKey = JSON.parse(localStorage.getItem("correctAnswers"));
  const [answerPaper, setAnswerPaper] = useState([]);
  const [confetti, setConfetti] = useState(true);
  const [dimensions, setDimensions] = useState({
    height: 650,
    width: 1262,
  });

  useEffect(() => {
    axios.get("http://localhost:3031/answers").then((res) => {
      console.log("RETURNED DATA :", JSON.stringify(res.data));
      setAnswerPaper(res.data);
    });
    switchOffConfetti();
  }, []);

  const switchOffConfetti = setTimeout(() => {
    setConfetti(false);
  }, 5000);

  return (
    <div className={classes.container}>
      {/* {confetti && (
        <Confetti
          numberOfPieces={200}
          width={dimensions.width}
          height={dimensions.height}
          tweenDuration={5000}
        />
      )} */}
      <h1>RESULTT</h1>
    </div>
  );
}

export default ResultPage;
