import React, { useEffect, useState } from "react";
import TriviaHeader from "../../../UI/Header/TriviaHeader";
import classes from "./QuizPage.module.css";
import { useAsyncError } from "react-router-dom";
import Question from "../Question/Question";

function QuizPage(props) {
  const [quiz, setQuiz] = useState([JSON.parse(localStorage.getItem("quiz"))]);
  const [quizData, setQuizData] = useState([]);
  const [category, setCategory] = useState("");

  useEffect(() => {
    console.log("QUIZDATATA:" + JSON.stringify(quiz));
    quiz.map((index) => {
      setCategory(index[0].category);
    });
  }, []);

  return (
    <div className={classes.container}>
      <div className={classes.headerContainer}>
        <div className={classes.category}>Category : {category}</div>
        <div className={classes.timer}>Time : </div>
      </div>
      <div className={classes.questionContainer}>
        <Question />
      </div>
      <h1>HESAD</h1>
    </div>
  );
}

export default QuizPage;
