import React, { useEffect, useState } from "react";
import TriviaHeader from "../../../UI/Header/TriviaHeader";
import classes from "./QuizPage.module.css";
import { useAsyncError } from "react-router-dom";
import Question from "../Question/Question";

function QuizPage(props) {
  const [quiz, setQuiz] = useState(JSON.parse(localStorage.getItem("quiz")));
  const [quizData, setQuizData] = useState([]);
  const [category, setCategory] = useState("");
  const [correctAnswers, setCorrectAnswers] = useState([]);
  // const correctAnswers = [];

  useEffect(() => {
    console.log("QUIZDATATA:" + JSON.stringify(quiz));
    quiz.map((index) => {
      setCategory(index.category);
    });

    for (let i = 0; i < quiz.length; i++) {
      setCorrectAnswers((ques) => [...ques, quiz[i].correct_answer]);
    }
  }, []);
  console.log("Quiz Object:" + JSON.stringify(correctAnswers));

  const listOfQuestions = quiz.map(function (question) {
    <Question
      question={question.question}
      incorrect={question.incorrect_answers}
      correct={question.correct_answer}
    />;
  });

  return (
    <div className={classes.container}>
      <div className={classes.headerContainer}>
        <div className={classes.category}>
          <b>Category :</b> {category}
        </div>
        {console.log("Correct Answers :" + JSON.stringify(correctAnswers))}
        <div className={classes.timer}>
          <b>Time : </b>
        </div>
      </div>
      <div className={classes.questionContainer}>
        <Question />
      </div>
      <div className={classes.change}>
        <div className={classes.changeButton1}>Previous</div>
        <div className={classes.changeButton2}>Next</div>
      </div>
      {/* <h1>HESAD</h1> */}
    </div>
  );
}

export default QuizPage;
