import { useScrollTrigger } from "@mui/material";
import React, { useState } from "react";
import classes from "./Question.module.css";

function Question(props) {
  const [quiz, setQuiz] = useState(props.state);
  const answers = [];
  answers.push(quiz.incorrect);
  answers.push(quiz.correct);

  console.log("ANSWERSS : " + JSON.stringify(answers));

  const shuffled = [...answers].sort(() => 0.5 - Math.random());

  shuffled.slice(0, 4);

  console.log("STATE : " + JSON.stringify(quiz));
  return (
    <div className={classes.container}>
      <div className={classes.question}>
        <h3>{quiz.index + 1}. </h3>
        <h3>{quiz.question}</h3>
      </div>
      <div className={classes.options}>
        <form>
          <div>
            <input type="radio" name="options" value="" />
            <label>1</label>
          </div>
          <div>
            <input type="radio" name="options" value="" />
            <label>1</label>
          </div>
          <div>
            <input type="radio" name="options" value="" />
            <label>1</label>
          </div>
          <div>
            <input type="radio" name="options" value="" />
            <label>1</label>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Question;
