import React, { useCallback, useEffect, useState } from "react";
import classes from "./QuizPage.module.css";
import Question from "../Question/Question";

function QuizPage(props) {
  const quiz = JSON.parse(localStorage.getItem("quiz"));
  const [category, setCategory] = useState("");
  const [correctAnswers, setCorrectAnswers] = useState([]);
  const [index, setIndex] = useState(0);
  const [quizState, setQuizState] = useState({
    question: quiz[index].question,
    correct: quiz[index].correct_answer,
    incorrect: quiz[index].incorrect_answers,
  });

  useEffect(() => {
    console.log("QUIZDATATA:" + JSON.stringify(quiz));
    quiz.map((index) => {
      setCategory(index.category);
    });

    for (let i = 0; i < quiz.length; i++) {
      setCorrectAnswers((ques) => [...ques, quiz[i].correct_answer]);
    }
  }, []);

  const handlePrevious = () => {
    setIndex(index - 1);
    for (index; ; ) {
      setQuizState({
        question: quiz[index].question,
        correct: quiz[index].correct_answer,
        incorrect: quiz[index].incorrect_answers,
      });
    }
  };

  const handleNext = () => {
    setIndex(index + 1);
    for (index; ; ) {
      setQuizState({
        index: index,
        question: quiz[index].question,
        correct: quiz[index].correct_answer,
        incorrect: quiz[index].incorrect_answers,
      });
    }
  };

  const handleState = useCallback(() => {
    return quizState;
  }, [quizState]);

  console.log("Quiz Object:" + JSON.stringify(quizState));

  return (
    <div className={classes.container}>
      <div className={classes.headerContainer}>
        <div className={classes.category}>
          <b>Category :</b> {category}
        </div>
        {console.log("Correct Answers :" + JSON.stringify(correctAnswers))}
        <div className={classes.timer}>
          <b>Time : </b>
        </div>
      </div>
      <div className={classes.questionContainer}>
        <Question state={handleState} />
      </div>
      <div className={classes.change}>
        <div className={classes.changeButton1} onClick={handlePrevious}>
          Previous
        </div>
        <div className={classes.changeButton2} onClick={handleNext}>
          Next
        </div>
      </div>
      {/* <h1>HESAD</h1> */}
    </div>
  );
}

export default QuizPage;
