import React, { useEffect, useState } from "react";
import classes from "./QuizPage.module.css";
import "./Question.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Dialog, DialogActions, DialogTitle } from "@mui/material";
import Stopwatch from "../StopWatch/Stopwatch";

function QuizPage(props) {
  const quiz = JSON.parse(localStorage.getItem("quiz"));
  const [category, setCategory] = useState("");
  const navigate = useNavigate();
  const [correctAnswers, setCorrectAnswers] = useState([]);
  const [index, setIndex] = useState(0);
  const [buttonState, setButtonState] = useState(false);
  const [open, setOpen] = useState(true);
  const [run, setRun] = useState(false);
  const [options, setOptions] = useState([]);
  const [checkState, setCheckState] = useState({
    check0: false,
    check1: false,
    check2: false,
    check3: false,
  });
  const [answerState, setAnswerState] = useState({
    index: index,
    answer: "",
  });
  const answers = [];
  answers.push(quiz[index].incorrect_answers[0]);
  answers.push(quiz[index].incorrect_answers[1]);
  answers.push(quiz[index].incorrect_answers[2]);
  answers.push(quiz[index].correct_answer);

  const handleClose = () => {
    setOpen(false);
    setRun(true);
  };

  useEffect(() => {
    // console.log("QUIZDATATA:" + JSON.stringify(quiz));
    quiz.map((index) => {
      setCategory(index.category);
    });

    for (let i = 0; i < quiz.length; i++) {
      setCorrectAnswers((ques) => [...ques, quiz[i].correct_answer]);
    }
    if (index === quiz.length - 1) {
      setButtonState(true);
    } else {
      setButtonState(false);
    }

    const shuffled = [...answers].sort(() => 0.5 - Math.random());
    setOptions(shuffled);
  }, [index]);

  const handleRadio = (e) => {
    setAnswerState({
      index: index + 1,
      answer: e.target.value,
    });
  };

  const handleNext = () => {
    if (index < quiz.length) {
      setIndex(index + 1);
    } else {
      setIndex(index);
    }
    axios.post("http://localhost:3031/answers", answerState).then((res) => {
      console.log("RESPONSE : " + JSON.stringify(res.data));
    });
    setCheckState({
      check0: false,
      check1: false,
      check2: false,
      check3: false,
    });
  };

  const resultHandler = () => {
    axios.post("http://localhost:3031/answers", answerState).then((res) => {
      console.log("RESPONSE : " + JSON.stringify(res.data));
    });
    setRun(false);
    // navigate("/");
  };

  return (
    <div className={classes.container}>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>{"Here We Go!"}</DialogTitle>
        <DialogActions>
          <button onClick={handleClose}>OK</button>
        </DialogActions>
      </Dialog>
      <div className={classes.headerContainer}>
        <div className={classes.category}>
          <b>Category :</b> {category}
        </div>
        {console.log("Correct Answers :" + JSON.stringify(correctAnswers))}
        <div className={classes.timer}>
          <b>Time : </b>
          <Stopwatch run={run} />
        </div>
      </div>
      <div className={classes.questionContainer}>
        {/* <Question state={handleState} /> */}
        {/* <div className={classes.container}> */}
        <div className="question">
          <h3>{index + 1}.&nbsp;&nbsp;&nbsp;&nbsp;</h3>

          <h3 dangerouslySetInnerHTML={{ __html: quiz[index].question }} />
        </div>
        <div className="options">
          <form>
            <div className="option">
              <input
                type="radio"
                name="options"
                value={options[0]}
                checked={checkState.check0}
                onChange={(e) => {
                  handleRadio(e);
                  setCheckState({
                    check0: true,
                  });
                }}
              />
              <label>{options[0]}</label>
            </div>
            <div className="option">
              <input
                type="radio"
                name="options"
                value={options[1]}
                checked={checkState.check1}
                onChange={(e) => {
                  handleRadio(e);
                  setCheckState({
                    check1: true,
                  });
                }}
              />
              <label>{options[1]}</label>
            </div>
            <div className="option">
              <input
                type="radio"
                name="options"
                value={options[2]}
                checked={checkState.check2}
                onChange={(e) => {
                  handleRadio(e);
                  setCheckState({
                    check2: true,
                  });
                }}
              />
              <label>{options[2]}</label>
            </div>
            <div className="option">
              <input
                type="radio"
                name="options"
                value={options[3]}
                checked={checkState.check3}
                // onClick={setCheckState({
                //   check3: true,
                // })}
                onChange={(e) => handleRadio(e)}
              />
              <label>{options[3]}</label>
            </div>
          </form>
        </div>
        {/* </div> */}
      </div>
      {!buttonState ? (
        <div className={classes.change}>
          {/* <div className={classes.changeButton1} onClick={handlePrevious}>
            <button>Previous</button>
          </div> */}
          <div className={classes.changeButton2} onClick={handleNext}>
            <button disabled={buttonState}>Next</button>
          </div>
        </div>
      ) : (
        <div className={classes.change}>
          {/* <div className={classes.changeButton1} onClick={handlePrevious}>
            <button>Previous</button>
          </div> */}
          <div className={classes.changeButton2}>
            <button onClick={resultHandler}>Submit</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default QuizPage;
