import React, { useCallback, useEffect, useState } from "react";
import classes from "./UpdateProfile.module.css";
import LoginPage from "../../LoginPage/LoginPage";
import TriviaHeader from "../../UI/Header/TriviaHeader";
import axios from "axios";
import { validPassword, validUsername } from "../../../Regex";
import { message } from "antd";

function UpdateProfile(props) {
  const [token, setToken] = useState(
    JSON.parse(sessionStorage.getItem("token"))
  );
  const [userData, setUserData] = useState(
    JSON.parse(localStorage.getItem("userData"))
  );
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [userName, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confPassword, setConfPassword] = useState("");
  const [usernameError, setUsernameError] = useState();
  const [passwordError, setPasswordError] = useState();
  const [passwordMatch, setpasswordMatch] = useState();
  let emailPlaceholder = userData.email === "" ? "Email.." : userData.email;

  useEffect(() => {
    if (!token) {
      {
        return <LoginPage />;
      }
    }
  }, []);

  const userNameValidation = useCallback(() => {
    if (userName.length !== 0 && !validUsername.test(userName)) {
      setUsernameError(true);
    } else {
      setUsernameError(false);
    }
  }, [userName]);

  const passWordValidation = useCallback(() => {
    if (password.length > 5 && !validPassword.test(password)) {
      setPasswordError(true);
    } else {
      setPasswordError(false);
    }
  }, [password]);

  const passWordMatch = useCallback(() => {
    if (confPassword !== password) {
      setpasswordMatch(true);
    } else {
      setpasswordMatch(false);
    }
  }, [confPassword]);

  const submitHandler = (e) => {
    e.preventDefault();
    axios
      .put(`http://localhost:3031/users/` + userData.id, {
        id: userData.id,
        firstName: firstName,
        lastName: lastName,
        email: email,
        password: confPassword,
      })
      .then((response) => {
        console.log("RESPONSE :" + JSON.stringify(response.data));
      });
    message.success("Profile updated successfully!");
  };

  return (
    <div className={classes.container}>
      <div>
        <TriviaHeader />
      </div>
      <div className={classes.outerbox}>
        <h1>Update Profile</h1>
        <div className={classes.wrapper}>
          <form onSubmit={submitHandler}>
            <div className={classes.doubleFormbox}>
              <div className={classes.temp}>
                <div className={classes.formbox}>
                  <label>Firstname</label>
                  <input
                    type="text"
                    value={firstName}
                    placeholder={userData.firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                  />
                </div>
              </div>
              <div className={classes.temp}>
                <div className={classes.formbox}>
                  <label>Lastname</label>
                  <input
                    type="text"
                    value={lastName}
                    placeholder={userData.lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    required
                  />
                </div>
              </div>
            </div>
            <div className={classes.doubleFormbox}>
              <div className={classes.temp}>
                <div className={classes.formbox2}>
                  <label>Username</label>
                  <input
                    type="text"
                    value={userName}
                    placeholder={userData.username}
                    onChange={(e) => setUserName(e.target.value)}
                    onFocus={(e) => {
                      userNameValidation();
                    }}
                    onBlur={(e) => {
                      userNameValidation();
                    }}
                    required
                  />
                  {/* {usernameError && <p>Username is invalid!</p>} */}
                </div>
                {usernameError && <p>Username is invalid!</p>}
              </div>
              <div className={classes.temp}>
                <div className={classes.formbox3}>
                  <label>Email</label>
                  <input
                    type="email"
                    value={email}
                    placeholder={emailPlaceholder}
                    onChange={(e) => setEmail(e.target.value)}
                    //   onFocus={(e) => {
                    //     emailValidation();
                    //   }}
                    //   onBlur={(e) => {
                    //     emailValidation();
                    //   }}
                    required
                  />
                </div>
              </div>
            </div>
            <div className={classes.doubleFormbox}>
              <div className={classes.temp}>
                <div className={classes.formbox}>
                  <label>Password</label>
                  <input
                    type="password"
                    value={password}
                    placeholder="Password.."
                    minLength={5}
                    maxLength={12}
                    onChange={(e) => setPassword(e.target.value)}
                    onFocus={(e) => {
                      passWordValidation();
                    }}
                    onBlur={(e) => {
                      passWordValidation();
                    }}
                    required
                  />
                  {/* {passwordError && <p>Password is invalid!</p>} */}
                </div>
                {passwordError && <p>Password is invalid!</p>}
              </div>
              <div className={classes.temp}>
                <div className={classes.formbox}>
                  <label>Confirm Password</label>
                  <input
                    type="password"
                    value={confPassword}
                    placeholder="Confirm Password.."
                    minLength={5}
                    maxLength={12}
                    onChange={(e) => setConfPassword(e.target.value)}
                    onFocus={(e) => {
                      passWordMatch();
                    }}
                    onBlur={(e) => {
                      passWordMatch();
                    }}
                    required
                  />
                  {/* {passwordMatch && <p>Passwords don't match!</p>} */}
                </div>
                {passwordMatch && <p>Passwords don't match!</p>}
              </div>
            </div>
            <button>Submit</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default UpdateProfile;
