  const [quiz, setQuiz] = useState(props.state);
  const question = props?.state?.question;
  const correct = props?.state?.correct;
  const incorrect = props?.state?.incorrect;
  return (
    <div>
      <h1>{question}</h1>
      <h1>HELLOOO</h1>
      <h1>{props?.correct}</h1>
      {console.log("INSIDE QUESTION!!!", props)}
      <h1>{props?.incorrect}</h1>
    </div>
  );
}

export default Question;
