import React, { useEffect, useState } from "react";
import classes from "./ResultPage.module.css";
import axios from "axios";
import Confetti from "react-confetti";

function ResultPage(props) {
  const answerKey = JSON.parse(localStorage.getItem("correctAnswers"));
  const [answerPaper, setAnswerPaper] = useState([]);
  const [confetti, setConfetti] = useState(true);
  const [dimensions, setDimensions] = useState({
    height: 650,
    width: 1262,
  });

  useEffect(() => {
    axios.get("http://localhost:3031/answers").then((res) => {
      console.log("RETURNED DATA :", JSON.stringify(res.data));
      setAnswerPaper(res.data);
    });
    setTimeout(() => {
      setConfetti(false);
    }, 5000);
  }, []);

  return (
    <div className={classes.container}>
      {confetti && (
        <div className={classes.confettiContainer}>
          <Confetti
            numberOfPieces={200}
            width={dimensions.width}
            height={dimensions.height}
          />
        </div>
      )}
      <h1>Results</h1>
      <div className={classes.resultContainer}>
        <h3>Hello</h3>
        <div className={classes.eachResult}>
          <h3>Total Questions</h3>
          <h3>:</h3>
          <h4>20</h4>
        </div>
        <div className={classes.eachResult}>
          <h3>Category</h3>
          <h3>:</h3>
          <h4>General K</h4>
        </div>
        <div className={classes.eachResult}>
          <h3>Difficulty</h3>
          <h3>:</h3>
          <h4>Easy</h4>
        </div>
        <div className={classes.eachResult}>
          <h3>Correct Answers </h3>
          <h3>:</h3>
          <h4>10</h4>
        </div>
      </div>
    </div>
  );
}

export default ResultPage;
