import React, { useState } from "react";
import classes from "./LoginPage.module.css";
import { useNavigate } from "react-router-dom";
import { validPassword, validUsername } from "../../Regex";

function LoginPage(props) {
  const [name, setName] = useState("");
  const [passWord, setPassword] = useState("");
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [usernameData, setUsernameData] = useState();
  const [usernameError, setUsernameError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const submitHandler = (e) => {
    e.preventDefault();

    console.log("ffff" + JSON.stringify(data));
    if (data) {
      data
        .filter((item) => {
          console.log("ITEM" + JSON.stringify(item));
          if (item.username === name) {
            setUsernameData(true);
          }
          console.log("USerNamedDTAR" + usernameData);
          return item.username === name;
        })
        .map(function (k) {
          console.log("INDEX" + JSON.stringify(k.firstName));
          if (passWord === k.password) {
            {
              axios.get("http://localhost:8080/login").then((response) => {
                console.log("token" + JSON.stringify(response.data));
                sessionStorage.setItem(
                  "token",
                  JSON.stringify(response.data.token)
                );
              });
              localStorage.setItem("userId", JSON.stringify(k.id));
              navigate("/user");
              console.log("ATLASTTT" + JSON.stringify(k.id));
            }
          } else {
            message.error("asdas");
          }
        });
    } else {
      // window.alert("No Data Found!");
      message.error("No Data found!");
    }
    if (!usernameData) {
      message.error("Invalid Credentials!");
    }
    setName("");
    setPassword("");
  };

  return (
    <div className={classes.loginPage}>
      <div className={classes.blankSpace}></div>
      <div className={classes.formData}>
        <div className={classes.loginForm}>
          <h1>Login</h1>
          <form onSubmit={submitHandler}>
            <div className={classes.formbox}>
              <label>UserName:</label>
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
                onFocus={(e) => {
                  setUsernameError(false);
                }}
                onBlur={(e) => {
                  if (!validUsername.test(name)) {
                    setUsernameError(true);
                  }
                }}
                placeholder="Username.."
              />
              {usernameError && <p>Username is invalid!</p>}
            </div>

            <div className={classes.formbox}>
              <label>Password:</label>
              <input
                type="password"
                value={passWord}
                minLength={5}
                maxLength={12}
                onChange={(e) => setPassword(e.target.value)}
                required
                onFocus={(e) => {
                  setPasswordError(false);
                }}
                onBlur={(e) => {
                  if (!validPassword.test(passWord)) {
                    setPasswordError(true);
                  }
                }}
                placeholder="Password.."
              />
              {passwordError && <p>Password is invalid!</p>}
            </div>

            <button>SUBMIT</button>

            <div className={classes.signup}>
              <p onClick={signUpHandler}>Ain't got a profile? Sign up here.</p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
