import React, { useState } from "react";
import classes from "./QuizHomePage.module.css";
import TriviaHeader from "../../../UI/Header/TriviaHeader";

function QuizHomePage(props) {
  const [token, setToken] = useState(
    JSON.parse(sessionStorage.getItem("token"))
  );
  const [questions, setQuestions] = useState();
  const [category, setCategory] = useState();
  const [difficulty, setDifficulty] = useState();
  const [setup, setSetup] = useState(true);

  const setupHandler = () => {};

  return (
    <div className={classes.container}>
      <div>
        <TriviaHeader token={token} />
      </div>
      <div className={classes.quizHomeContainer}>
        <h1>Take a Quiz</h1>
        {setup ? (
          <div className={classes.formBox}>
            <form onSubmit={setupHandler}>
              <div className={classes.selections}>
                <label>Total Questions :</label>
                <input
                  type="number"
                  min={5}
                  max={20}
                  onChange={(e) => setQuestions(e.target.value)}
                />
              </div>
              <div className={classes.selections}>
                <label>Category :</label>
                <select
                  name="Category"
                  className={classes.selectbox}
                  onChange={(event) => setCategory(event.target.value)}
                  required
                >
                  <option disabled selected value>
                    Select a category..
                  </option>
                  <option value="9">General Knowledge</option>
                  <option value="17">Science & Nature</option>
                  <option value="21">Sports</option>
                  <option value="22">Geography</option>
                  <option value="23">History</option>
                  <option value="24">Politics</option>
                  <option value="25">Art</option>
                  <option value="27">Animals</option>
                  <option value="28">Vehicles</option>
                </select>
              </div>
              <div className={classes.selections}>
                <label>Difficulty :</label>
                <select
                  name="Difficulty"
                  className={classes.selectbox}
                  onChange={(event) => setDifficulty(event.target.value)}
                >
                  <option disabled selected value>
                    Select difficulty level..
                  </option>
                  <option value="easy">Easy</option>
                  <option value="medium">Medium</option>
                  <option value="hard">Hard</option>
                </select>
              </div>
              <button>Submit</button>
            </form>
          </div>
        ) : (
          <div className={classes.formBox}> </div>
        )}
      </div>
    </div>
  );
}

export default QuizHomePage;
