import { useScrollTrigger } from "@mui/material";
import React, { useState } from "react";
import classes from "./Question.module.css";

function Question(props) {
  const [quiz, setQuiz] = useState(props.state);
  const answers = [];
  answers.push(quiz.incorrect[0]);
  answers.push(quiz.incorrect[1]);
  answers.push(quiz.incorrect[2]);
  answers.push(quiz.correct);

  const shuffled = [...answers].sort(() => 0.5 - Math.random());

  const questions = shuffled.slice(0, 4);

  console.log("ANSWERSS : " + JSON.stringify(questions));
  console.log("STATE : " + JSON.stringify(quiz));
  return (
    <div className={classes.container}>
      <div className={classes.question}>
        <h3>{quiz.index + 1}.&nbsp;&nbsp;&nbsp;&nbsp;</h3>
        <h3>{quiz.question}</h3>
      </div>
      <div className={classes.options}>
        <form>
          <div>
            <input type="radio" name="options" value={questions[0]} />
            <label>{questions[0]}</label>
          </div>
          <div>
            <input type="radio" name="options" value={questions[1]} />
            <label>{questions[1]}</label>
          </div>
          <div>
            <input type="radio" name="options" value={questions[2]} />
            <label>{questions[2]}</label>
          </div>
          <div>
            <input type="radio" name="options" value={questions[3]} />
            <label>{questions[3]}</label>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Question;
