import React, { useCallback, useEffect, useState } from "react";
import classes from "./QuizPage.module.css";
import "../Question/Question.css";

function QuizPage(props) {
  const quiz = JSON.parse(localStorage.getItem("quiz"));
  const [category, setCategory] = useState("");
  const [correctAnswers, setCorrectAnswers] = useState([]);
  const [index, setIndex] = useState(0);
  const [buttonState, setButtonState] = useState(false);
  const answers = [];
  answers.push(quiz[index].incorrect_answers[0]);
  answers.push(quiz[index].incorrect_answers[1]);
  answers.push(quiz[index].incorrect_answers[2]);
  answers.push(quiz[index].correct_answer);

  const shuffled = [...answers].sort(() => 0.5 - Math.random());

  const questions = shuffled.slice(0, 4);

  useEffect(() => {
    console.log("QUIZDATATA:" + JSON.stringify(quiz));
    quiz.map((index) => {
      setCategory(index.category);
    });

    for (let i = 0; i < quiz.length; i++) {
      setCorrectAnswers((ques) => [...ques, quiz[i].correct_answer]);
    }
  }, []);

  const handlePrevious = () => {
    setIndex(index - 1);
  };

  const handleNext = () => {
    if (index < quiz.length) {
      setIndex(index + 1);
    } else {
      setIndex(index);
    }
  };

  return (
    <div className={classes.container}>
      <div className={classes.headerContainer}>
        <div className={classes.category}>
          <b>Category :</b> {category}
        </div>
        {console.log("Correct Answers :" + JSON.stringify(correctAnswers))}
        <div className={classes.timer}>
          <b>Time : </b>
        </div>
      </div>
      <div className={classes.questionContainer}>
        {/* <Question state={handleState} /> */}
        {/* <div className={classes.container}> */}
        <div className="question">
          <h3>{index + 1}.&nbsp;&nbsp;&nbsp;&nbsp;</h3>
          <h3>{quiz[index].question}</h3>
        </div>
        <div className="options">
          <form>
            <div className="option">
              <input type="radio" name="options" value={questions[0]} />
              <label>{questions[0]}</label>
            </div>
            <div className="option">
              <input type="radio" name="options" value={questions[1]} />
              <label>{questions[1]}</label>
            </div>
            <div className="option">
              <input type="radio" name="options" value={questions[2]} />
              <label>{questions[2]}</label>
            </div>
            <div className="option">
              <input type="radio" name="options" value={questions[3]} />
              <label>{questions[3]}</label>
            </div>
          </form>
        </div>
        {/* </div> */}
      </div>
      <div className={classes.change}>
        <div>
          <button className={classes.changeButton1} onClick={handlePrevious}>
            Previous
          </button>
        </div>
        <div className={classes.changeButton2} onClick={handleNext}>
          <button disabled={buttonState}>Next</button>
        </div>
      </div>
      {/* <h1>HESAD</h1> */}
    </div>
  );
}

export default QuizPage;
